import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'

class TodoApp extends Component {
    render() {
        return(
            <div className='TodoApp'>
                <Router>
                    <>
                        <HeaderComponent/>
                        <Switch>
                        <Route path="/" exact component={LoginComponent}/>
                        <Route path="/login" component={LoginComponent}/>
                        <Route path="/welcome/:name" component={WelcomeComponent}/>
                        <Route path="/todos" component={ListTodosComponent}/>
                        <Route component={ErrorComponent}/>
                        </Switch>
                        <FooterComponent/>
                    </>
                </Router>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return(
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="/" className="navbar-brand">Śliwa</a></div>
                    <ul className="navbar-nav">
                        <li className="nav-link"><Link to='/welcome'>Home</Link></li>
                        <li className="nav-link">Todos</li>
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li className="nav-link">Login</li>
                        <li className="nav-link">Logout</li>
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return(
            <div>
                <hr/>
                Footer
            </div>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            todos :
            [
                {id: 1, description: 'Learn React', done:false, targetDate: new Date()},
                {id: 2, description: 'Learn sas', done:false, targetDate: new Date()},
                {id: 3, description: 'Learn dfsft', done:false, targetDate: new Date()}
            ]
             
        }
    }
    render() {
    return(<div>
             <h1>List Todos</h1>
             <table>
                 <thead>
                     <tr>
                         <th>id</th>
                         <th>description</th>
                         <th>Targed Date</th>
                         <th>Is Completed?</th>
                     </tr>
                 </thead>
                 <tbody>
                    {
                        this.state.todos.map(
                            todo =>
                            <tr>
                                <td>{todo.id}</td>
                                <td>{todo.description}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.targetDate.toString()}</td>
                            </tr>
                        )
                    }
                 </tbody>
             </table>
        
        
        </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
    return(<div>
        Welcome {this.props.match.params.name}! You can manage your todos <Link to='/todos'>here</Link>
        </div>)
    }
}

function ErrorComponent() {
    
    return (<div>Error Ocurred.</div>)
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username : '123',
            password : '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)

    }

    handleChange(event) {
        console.log(event.target.value);
        this.setState({
            [event.target.name] : event.target.value
        }) 
    }

    loginClicked() {
        if(this.state.username==='123' && this.state.password===''){

            this.props.history.push(`/welcome/${this.state.username}`)
            // this.setState({showSuccessMessage: true})
            // this.setState({hasLoginFailed: false})
        } else {
            this.setState({showSuccessMessage: false})
            this.setState({hasLoginFailed: true})
        }
        

    }

    render() {
        return(
            <div>
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login Sucessful</div>}     
            User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
            Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
            <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }

}



export default TodoApp
