import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'
import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from './AuthenticatedRoute'
import LoginComponent from './LoginComponent'

class TodoApp extends Component {
    render() {
        return(
            <div className='TodoApp'>
                <Router>
                    <>
                        <HeaderComponent/>
                        <Switch>
                        <Route path="/" exact component={LoginComponent}/>
                        <Route path="/login" component={LoginComponent}/>
                        <AuthenticatedRoute path="/welcome/:name" component={WelcomeComponent}/>
                        <AuthenticatedRoute path="/todos" component={ListTodosComponent}/>
                        <AuthenticatedRoute path="/logout" component={LogoutComponent}/>
                        <Route component={ErrorComponent}/>
                        </Switch>
                        <FooterComponent/>
                    </>
                </Router>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        const isUserLoggedIn = AuthenticationService.isUserLoggedIn();
        return(
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="/" className="navbar-brand">Åšliwa</a></div>
                    <ul className="navbar-nav">
                    {isUserLoggedIn && <li><Link to='/welcome/123' className="nav-link">Home</Link></li>}
                    {isUserLoggedIn && <li><Link to='/todos' className="nav-link">Todos</Link></li>}
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                    {!isUserLoggedIn && <li><Link to='/login' className="nav-link">Login</Link></li>}
                    {isUserLoggedIn && <li><Link to='/logout' className="nav-link" onClick={AuthenticationService.logout}>Logout</Link></li>}
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return(
            <footer className="footer">
                <span className="text-muted">All Rights Reserved</span>
            </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return(
            <>
                <h1>You are logged out</h1>
                <div className="container">Thank you for using this app</div>
            </>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            todos :
            [
                {id: 1, description: 'Learn React', done:false, targetDate: new Date()},
                {id: 2, description: 'Learn sas', done:false, targetDate: new Date()},
                {id: 3, description: 'Learn dfsft', done:false, targetDate: new Date()}
            ]
             
        }
    }
    render() {
    return(
        <div>
             <h1>List Todos</h1>
             <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Target Date</th>
                            <th>Is Completed?</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map(
                                todo =>
                                <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
             </div>
        </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return(
            <>
            <h1>Welcome!</h1>
            <div className="container">
            Welcome {this.props.match.params.name}! You can manage your todos <Link to='/todos'>here</Link>
            </div>
            </>
        )
    }
}

function ErrorComponent() {
    
    return (<div>Error Ocurred. <Link to="/login">Go back!</Link></div>)
}





export default TodoApp
